```
// ... mango.ts , the little wrapper with utils for tests
 async setupMangoGroup() {
    this.group = await this.client.getMangoGroup(this.mangoGroupKey);
    this.group.rootBankAccounts = await this.group.loadRootBanks(this.client.connection);
    this.group.mangoCache = (await this.group.loadCache(this.client.connection)).publicKey;
  }

  getTokenIndex(tokenMint: PublicKey): number {
    // look up token index by mint public key
    return this.group.getTokenIndex(tokenMint);
  }

  getTokenConfig(tokenIndex: number): TokenConfig {
    return this.groupConfig.tokens.find((_config, index) => {
      return index == tokenIndex;
    });
  }

  getRootBankForToken(tokenIndex: number): PublicKey {
    const rootBank = this.group.rootBankAccounts[tokenIndex];
    if (!rootBank) {
      throw new Error("vault is undefined");
    }
    return rootBank.publicKey;
  }

  getNodeBankFor(tokenIndex: number): PublicKey {
    const tokenConfig = this.getTokenConfig(tokenIndex);
    const nodeBank = this.group.rootBankAccounts[tokenIndex].nodeBankAccounts.find((node, _index) => {
      if (!node) {
        return false;
      }
      return node.publicKey.toBase58 == tokenConfig.rootKey.toBase58;
    });
    if (!nodeBank) {
      throw new Error("nodeBank is undefined");
    }
    return nodeBank.publicKey;
  }

  getVaultFor(tokenIndex: number): PublicKey {
    const vault = this.group.rootBankAccounts[tokenIndex].nodeBankAccounts[0].vault;
    if (!vault) {
      throw new Error("vault is undefined");
    }
    return vault;
  }

  getMangoCache(): PublicKey {
    return this.group.mangoCache;
  }
// ...
```


 With this in place, the deposit is working, doing CPI from my program.

```
// ... inside the Solana program doing the CPI call       
let instruction = solana_program::instruction::Instruction {
            program_id: ctx.accounts.mango_program.key(),
            data: mango::instruction::MangoInstruction::Deposit {
                quantity: coin_amount,
            }
            .pack(),
            accounts: vec![
                AccountMeta::new_readonly(ctx.accounts.mango_group.key(), false),
                AccountMeta::new(ctx.accounts.mango_account.key(), false),
                AccountMeta::new_readonly(ctx.accounts.depository_record.key(), true),
                AccountMeta::new_readonly(ctx.accounts.mango_cache.key(), false),
                AccountMeta::new_readonly(ctx.accounts.mango_root_bank.key(), false),
                AccountMeta::new(ctx.accounts.mango_node_bank.key(), false),
                AccountMeta::new(ctx.accounts.mango_vault.key(), false),
                AccountMeta::new_readonly(ctx.accounts.token_program.key(), false),
                AccountMeta::new(ctx.accounts.user_deposited_coin.key(), false),
            ],
        };

        let account_infos = [
            ctx.accounts.mango_program.to_account_info(),
            ctx.accounts.mango_group.to_account_info(),
            ctx.accounts.mango_account.to_account_info(),
            ctx.accounts.depository_record.to_account_info(),
            ctx.accounts.mango_cache.to_account_info(),
            ctx.accounts.mango_root_bank.to_account_info(),
            ctx.accounts.mango_node_bank.to_account_info(),
            ctx.accounts.mango_vault.to_account_info(),
            ctx.accounts.token_program.to_account_info(),
            ctx.accounts.user_deposited_coin.to_account_info(),
        ];

        solana_program::program::invoke_signed(
            &instruction,
            &account_infos,
            pda_signer_seed,
        )?;
